import React, { useEffect, useRef, useState } from 'react';
import './style.css'
export const DNDCard = ({ id, letter, x, setConuter, block }) => {
    const [coordinates, setCoordinates] = useState({ x: 0, y: 0 })
    const [moveRoot, setMoveRoot] = useState(true)
    const [blockCoordinate, setBlockCoordinate] = useState([]);
    const [disabledBlock, setDisabledBlock]=useState([])

    useEffect(() => {
        setTimeout(() => {
            const arr = []
            console.log(block)
            block.current.querySelectorAll('.drop-zone').forEach((item, i) => {
                const rect = item.getBoundingClientRect();
                const { innerText } = item
                const { left, right, bottom, top } = rect
                if(i===0){
                    console.log(left)
                }
                arr.push({ left: left-block.current.offsetLeft, right, top, bottom, innerText })
            })
            setBlockCoordinate(arr)
        }, 100)

    }, [])


    useEffect(() => {
        setCoordinates({ x, y: 0 })
    }, [x])

    const styleTag = {
        position: 'absolute',
        top: coordinates.y,
        left: coordinates.x
    };
    const onMouseDown = (e) => {
        e.preventDefault();
        let shiftX = e.clientX - coordinates.x;
        let shiftY = e.clientY - coordinates.y;

        const moveAt = (pageX, pageY) => {
            if (moveRoot) {
                let x = pageX - shiftX;
                let y = pageY - shiftY;
                if (x < 0) {
                    x = 0;
                }
                if (y < 0) {
                    y = 0;
                }

                blockCoordinate.forEach(({ top, left, right, bottom, innerText }, i) => {

                    if (x > left && x + 80 < right && y > top && y + 80 < bottom && innerText.toLowerCase() === letter.toLowerCase() && !disabledBlock.includes(i)) {
                        setConuter((prev) => {
                            return prev + 1
                        })

                        x = left + 12.5
                        y = top + 12.5
                        window.removeEventListener('mousemove', onMouseMove);
                        setDisabledBlock((prev)=>{
                            return [...prev, i]
                        })
                        setMoveRoot(false)
                    }
                })



                setCoordinates({ x: x, y: y });
            }

        };
        moveAt(e.clientX, e.clientY);

        const onMouseMove = (event) => {

            moveAt(event.clientX, event.clientY);


        };

        window.addEventListener('mousemove', onMouseMove);

        window.addEventListener('mouseup', () => {
            window.removeEventListener('mousemove', onMouseMove);
        });
    };


    return (
        <div
            id={id}
            onMouseDown={onMouseDown}
            // onDragStart={ondragstart}
            className='drop-card'
            style={styleTag}
        // ref={ball}
        >
            {/* <div '> */}
            {letter}

            {/* </div> */}
        </div>
    );
};
