import React, { useState, useEffect, useMemo, useRef } from 'react';
import { DNDCard } from '../DNDCard';
import { nanoid } from 'nanoid';
import './style.css'

export const WordsComponent = ({ data, right=false }) => {
    const [currentWordNum, setCurrentWordNum] = useState(0)
    const [counter, setCounter] = useState(0)
    const [showButton, setShowButton] = useState(false)
    const [disabledBlock, setDisabledBlock]=useState([])

    const ref=useRef()
    const currentWord = data[currentWordNum]


    const renderDndCard = useMemo(() => {
        return currentWord.first.split('').map((item, index) => {
            return <DNDCard block={ref} key={nanoid()} x={60 * index} letter={item} id={nanoid()} setConuter={setCounter} />
        })

    }, [currentWordNum, currentWord])


    const renderDropZone = useMemo(() => {
        return currentWord.second.split('').map(item => {
            return <div key={nanoid()} className='drop-zone'>{item}</div>
        })

    }, [currentWordNum])

    useEffect(() => {

        if (counter === currentWord.second.length) {
            setShowButton(true)
        } else {
            setShowButton(false)
        }
    }, [counter])

    const click = () => {
        setCurrentWordNum(currentWordNum + 1)
        setCounter(0)
        setShowButton(false)
    }
    return (

        <div ref={ref} className={`wrapper wrapper-words ${right ? 'right-wrapper' : ''}`} >
            <div className='flex-container absolute-letter'>
                {renderDndCard}

            </div>
            <div>
                <div className='flex-container'>
                    {renderDropZone}

                </div>

                <button className='next-button' style={{ "opacity": showButton ? 1 : 1 }} onClick={click} >Далее</button>


            </div>

        </div>
    );
};
