import React, { useState, useEffect } from 'react';
import Input from '../Input/Input';
import './style.css'
export const RebusContainer = ({ data, right }) => {
    const [currentNum, setCurrentNum] = useState(0)
    const [value, setValue] = useState('');
    const [showButton, setShowButton] = useState(false)
    const currentRebus = data[currentNum]

    // const click=()=>{
    //     if(value.toLowerCase()===currentRebus.answer.toLowerCase()){
    //         console.log("верно")
    //     }
    // }
    useEffect(() => {
        if (value.toLowerCase() === currentRebus.answer.toLowerCase()) {
            setShowButton(true)
        }
    }, [value])
    console.log(value)
    return (
        <div className={`wrapper wrapper-words ${right ? 'right-wrapper' : ''}`} >
            <div className='flex-container-column'>
                <img className='image' src={currentRebus.img} alt='img' />
                {/* <input value={value} onChange={(e) => setValue(e.target.value)} /> */}
                <Input onChange={setValue} value={value} />
                {showButton &&
                    <div>
                        <h2>{`Верно, это ${currentRebus.answer}`}</h2>
                        <button
                            onClick={() => {
                                setCurrentNum(currentNum + 1)
                                setShowButton(false)
                            }}>
                            Далее
                        </button>

                    </div>
                }
            </div>

        </div>
    );
};
