import React, {useState, useEffect, useMemo} from 'react';
import { DNDCard } from '../DNDCard';
import { nanoid } from 'nanoid';
import './style.css'

export const Words = ({data}) => {
    const [currentWordNum, setCurrentWordNum] = useState(0)
    const [counter, setCounter]=useState(0)
    const [showButton,setShowButton]=useState(false)

    const currentWord = data[currentWordNum]


    const renderDndCard = useMemo(() => {
        return currentWord.first.split('').map((item, index) => {
            return <DNDCard key={nanoid()} x={60 * index} letter={item} id={item + index} setConuter={setCounter} />
        })

    }, [currentWordNum])

    const renderDropZone = useMemo(() => {
       return currentWord.second.split('').map(item=>{
            return <div className='drop-zone'>{item}</div>
        })
        // const arr = []
        // for (let i = 0; i < currentWord.first.length; i++) {
        //     arr.push(<div className='drop-zone'></div>)
        // }
        // return arr
    }, [currentWordNum])

    useEffect(()=>{

     if(counter===currentWord.second.length){
        setShowButton(true)
     }else{
        setShowButton(false)
     }
    },[counter])

    const click=()=>{
        setCurrentWordNum(currentWordNum+1)
        setCounter(0)
        setShowButton(false)
    }
    return (
        <div className='wrapper' >
        <div className='flex-container '>
            {renderDndCard}

        </div>
        <div>
        <div className='flex-container'>
            {renderDropZone}

        </div>

        <button style={{"opacity": showButton ? 1:0}} onClick={click} >Далее</button>


        </div>

    </div>
    );
};
