import React, { useEffect, useRef, useState } from 'react';
import './style.css'
export const DNDCard = ({ id, letter, x }) => {
    const [coordinates, setCoordinates] = useState({ x: 0, y: 0 })
    const [blockCoordinate, setBlockCoordinate] = useState([]);

    useEffect(() => {
        if(document.onload && document.querySelectorAll('.drop-zone')){

        }

    }, [blockCoordinate])

    document.onload(()=>{
        setBlockCoordinate(document.querySelectorAll('.drop-zone').map(item => {
            const rect = item.getBoundingClientRect();
            const { left, right, bottom, top } = rect
            return { left, right, top, bottom }
        }))
    })

    console.log(blockCoordinate)

    useEffect(() => {
        setCoordinates({ x, y: 0 })
    }, [x])

    const styleTag = {
        position: 'absolute',
        top: coordinates.y,
        left: coordinates.x
    };
    const onMouseDown = (e) => {
        e.preventDefault();
        // setSaveTrigger(false);
        let shiftX = e.clientX - coordinates.x;
        let shiftY = e.clientY - coordinates.y;

        const moveAt = (pageX, pageY) => {
            let x = pageX - shiftX;
            let y = pageY - shiftY;
            if (x < 0) {
                x = 0;
            }
            //   if (x > block.offsetWidth) {
            //     x = block.offsetWidth;
            //   }
            //   if (y > block.offsetHeight - 3) {
            //     y = block.offsetHeight - 3;
            //   }
            if (y < 0) {
                y = 0;
            }
            setCoordinates({ x: x, y: y });
        };
        moveAt(e.clientX, e.clientY);

        const onMouseMove = (event) => {
            moveAt(event.clientX, event.clientY);
        };

        window.addEventListener('mousemove', onMouseMove);

        window.addEventListener('mouseup', () => {
            window.removeEventListener('mousemove', onMouseMove);
            //   setSaveTrigger(true);
        });
    };


    return (
        <div
            id={id}
            onMouseDown={onMouseDown}
            // onDragStart={ondragstart}
            className='drop-card'
            style={styleTag}
        // ref={ball}
        >
            {/* <div '> */}
            {letter}

            {/* </div> */}
        </div>
    );
};
