import React, { useState, useMemo } from 'react';
import { data } from './_mock_'
import './style.css'
import { DNDCard } from '../../components/DNDCard';
import { Container } from '../../components/Container';


export const Words = () => {
    const [currentWordNum, setCurrentWordNum] = useState(0)

    const currentWord = data[currentWordNum]

    const renderDndCard = useMemo(() => {
        return currentWord.first.split('').map((item, index) => {
            return <DNDCard x={60 * index} letter={item} id={item + index} />
        })

    }, [currentWordNum])

    const renderDropZone = useMemo(() => {
        const arr = []
        for (let i = 0; i < currentWord.first.length; i++) {
            arr.push(<div className='drop-zone'></div>)
        }
        return arr
    }, [currentWordNum])

    return (
        <Container
            left={<div className='wrapper' >
                <div className='flex-container '>
                    {renderDndCard}

                </div>
                <div className='flex-container'>
                    {renderDropZone}

                </div>
            </div>}
        />

    );
};
